${((outputs) => {return outputs.deployments
  .filter(deployment => (
    (environmentField.value === 'All' || deployment.environment?.name === environmentField.value) &&
    (statusField.value === 'All' || deployment.status === statusField.value)
  ))
  .map(deployment => {
    // Check if deployable and pipeline exist to avoid errors
    if (deployment.deployable?.pipeline) {
      const { id, web_url } = deployment.deployable.pipeline;

      // Return a new object with the original deployment and the 'links' field
      return {
        ...deployment,
        links: {
          label: id,
          href: web_url
        }
      };
    }

    // If no pipeline, return the deployment as is
    return deployment;
  });
})(self.rawOutputs)}

${(() => {function filterDeployments(deployments, environment, status, createdDate) {
  if (!createdDate || !createdDate.start || !createdDate.end) {
    throw new Error("Invalid createdDate structure. Ensure it has both 'start' and 'end' properties.");
  }

  const startDate = new Date(createdDate.start); // Convert start timestamp to Date
  const endDate = new Date(createdDate.end); // Convert end timestamp to Date

  return deployments.filter(deployment => {
    const deploymentDate = new Date(deployment.created_at);

    // Check if the deployment matches the environment, status, and falls within the date range
    return deployment.environment.name === environment && 
           deployment.status === status &&
           deploymentDate >= startDate && 
           deploymentDate <= endDate;
  });
}

