  "description": "Pie chart of Merge Requests by status with custom colors",
  "data": {
    "name" : "dataSource"
  },
  "transform": [
    {
      "window": [
        { "op": "sum", "field": "count", "as": "total" }
      ],
      "frame": [null, null]
    },
    {
      "calculate": "datum.count / datum.total * 100",
      "as": "percent"
    }
  ],
  "mark": {
    "type": "arc",
    "tooltip": true
  },
  "encoding": {
    "theta": {
      "field": "count",
      "type": "quantitative",
      "stack": "normalize"
    },
    "color": {
      "field": "status",
      "type": "nominal",
      "title": "State",
      "scale": {
        "domain": ["merged", "closed", "opened", "locked"],
        "range": ["green", "red", "yellow", "gray"]
      }
    },
    "tooltip": [
      { "field": "status", "type": "nominal", "title": "Status" },
      { "field": "count", "type": "quantitative", "title": "Count" },
      {
        "field": "percent",
        "type": "quantitative",
        "format": ".2f",
        "title": "Percentage (%)"
      }
    ]
  }
}

