properties.outputs:
${((outputs) => {function getRelativeTime(creationTimestamp) {
    const diffMs = Date.now() - new Date(creationTimestamp).getTime(); // Time difference in ms

    const seconds = Math.floor(diffMs / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);

    const remainingHours = hours % 24;
    const remainingMinutes = minutes % 60;
    const remainingSeconds = seconds % 60;

    if (days > 0) {
        return `${days}d${remainingHours}h`; // Days and hours
    } else if (hours > 0) {
        return `${hours}h${remainingMinutes}m`; // Hours and minutes
    } else if (minutes > 0) {
        return `${minutes}m${remainingSeconds}s`; // Minutes and seconds
    } else {
        return `${seconds}s`; // Just seconds
    }
}

return outputs.items.map(deployment => {
    // Safely access annotations with optional chaining
    const annotations = deployment.spec.template.metadata?.annotations || {};
    const restartTimestamp = annotations["kubectl.kubernetes.io/restartedAt"];

    return {
        name: deployment.metadata.name, // Deployment name
        replicas: deployment.status.replicas || 0, // Total replicas
        readyReplicas: `${deployment.status.readyReplicas || 0}/${deployment.status.replicas || 0}`, // Ready replicas / Total replicas
        updatedReplicas: deployment.status.updatedReplicas || 0, // Updated replicas
        availableReplicas: deployment.status.availableReplicas || 0, // Available replicas
        image: deployment.spec.template.spec.containers?.[0]?.image || "Unknown", // Container image
        revision: deployment.metadata.annotations?.["deployment.kubernetes.io/revision"] || "Unknown", // Deployment revision
        available: deployment.status.conditions?.[0]?.status || "Unknown", // Default to "Unknown" if status is missing
        progressing: deployment.status.conditions?.[1]?.status || "Unknown", // Default to "Unknown" if status is missing
        age: deployment.metadata.creationTimestamp ? getRelativeTime(deployment.metadata.creationTimestamp) : "N/A", // Correctly formatted age
        sinceRestart: restartTimestamp ? getRelativeTime(restartTimestamp) : "N/A", // Relative time since last restart
        lastRestartTime: restartTimestamp ? restartTimestamp : "N/A" // Time of last restart
    };
});
})(self.rawOutputs)}

${((outputs) => {function getRelativeTime(timestamp) {
    const diffMs = Date.now() - new Date(timestamp).getTime(); // Time difference in ms

    const seconds = Math.floor(diffMs / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);

    const remainingHours = hours % 24;
    const remainingMinutes = minutes % 60;
    const remainingSeconds = seconds % 60;

    if (days > 0) {
        return `${days}d${remainingHours}h`; // Days and hours
    } else if (hours > 0) {
        return `${hours}h${remainingMinutes}m`; // Hours and minutes
    } else if (minutes > 0) {
        return `${minutes}m${remainingSeconds}s`; // Minutes and seconds
    } else {
        return `${seconds}s`; // Just seconds
    }
}

return outputs.items
    .filter(pod => {
        // Get the ReplicaSet name from the ownerReferences
        const ownerReference = pod.metadata.ownerReferences?.find(owner => owner.kind === "ReplicaSet");

        if (!ownerReference) {
            // Skip pods without an ownerReference
            return false;
        }

        // Extract the name of the ReplicaSet
        const replicaSetName = ownerReference.name;

        // Check if the ReplicaSet name starts with the selected Deployment name
        return replicaSetName.startsWith(deploymentsTable.selectedRow.content.name);
    })
    .map(pod => {
        // Map the Pod data to the desired columns
        return {
            name: pod.metadata.name, // Pod name
            status: pod.status.phase, // Pod status (Running, Pending, etc.)
            ready: pod.status.conditions?.find(condition => condition.type === "Ready")?.status || "Unknown", // Pod readiness
            restarts: pod.status.containerStatuses?.reduce((total, container) => total + (container.restartCount || 0), 0) || 0, // Total restarts
            node: pod.spec.nodeName, // Node where the Pod is running
            podIP: pod.status.podIP, // Pod IP address
            age: pod.status.startTime ? getRelativeTime(pod.status.startTime) : "N/A" // Pod start time as relative age
        };
    });
})(self.rawOutputs)}

${((outputs) => {const containerStatuses = outputs.status.containerStatuses;

return outputs.spec.containers.map(container => {
    // Find the corresponding status for the container
    const containerStatus = containerStatuses?.find(status => status.name === container.name);

    // Determine the state based on containerStatus
    const containerState = containerStatus?.state?.running
        ? "Running"
        : containerStatus?.state?.waiting
        ? "Waiting"
        : containerStatus?.state?.terminated
        ? "Terminated"
        : "Unknown";

    return {
        name: container.name, // Container name
        image: container.image, // Container image and tag
        state: containerState, // Running, Waiting, or Terminated state
        reason: containerStatus?.state?.waiting?.reason || 
                containerStatus?.state?.terminated?.reason || 
                "N/A", // Reason for Waiting or Terminated states
        restarts: containerStatus?.restartCount || 0, // Restart count
        ready: containerStatus?.ready ? "True" : "False", // Readiness status
        started: containerStatus?.state?.running?.startedAt ? "True" : "False", // Started status
        ports: container.ports?.map(port => `${port.containerPort}/${port.protocol}`).join(", ") || "N/A" // Exposed ports
    };
});
})(self.rawOutputs)}

