action.value:
-- Set the session name here DECLARE @session_name NVARCHAR(100) = 'datadog_sql_statement';
-- See size of ring buffer SELECT DATALENGTH(target_data) / 1024.0 AS ring_buffer_kb FROM sys.dm_xe_session_targets AS t JOIN sys.dm_xe_sessions AS s ON t.event_session_address = s.address WHERE s.name = @session_name AND t.target_name = 'ring_buffer';
-- -- Minimal polling of session events -- SELECT -- event_data.query('.') AS full_event_xml -- FROM ( -- SELECT CAST(t.target_data AS XML) AS target_xml -- FROM sys.dm_xe_session_targets AS t -- JOIN sys.dm_xe_sessions AS s -- ON t.event_session_address = s.address -- WHERE s.name = @session_name -- AND t.target_name = 'ring_buffer' -- ) AS src -- CROSS APPLY target_xml.nodes('//RingBufferTarget/event[position() <= 100]') AS XTbl(event_data);
SELECT event_data.value('(event/@timestamp)[1]', 'datetime2') AS event_timestamp, event_data.query('.') AS full_event_xml FROM ( SELECT CAST(t.target_data AS XML) AS target_xml FROM sys.dm_xe_session_targets AS t JOIN sys.dm_xe_sessions AS s ON t.event_session_address = s.address WHERE s.name = @session_name AND t.target_name = 'ring_buffer' ) AS src CROSS APPLY target_xml.nodes('//RingBufferTarget/event[@name="attention"]') AS XTbl(event_data) ORDER BY event_timestamp;
